package Program;

public class SwappingwithoutThirdVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=3;
		int b=7;
	    a=a+b;
		b=a-b;
		a=a-b;
		
		System.out.println("Value of a after swapping " +a);
		System.out.println("Value of b after swapping " +b);
		
		
	}

}
